import React, { useEffect, useState } from 'react'
import { useHistory } from 'react-router-dom'

//components
import { api } from '../../Api/app'
import messageStore from '../../Store/MessageStore'

//scss
import './Login.scss'


export default function Login(props) {

    const history = useHistory()

    const [login, setLogin] = useState('kaique@tropa.digital')
    const [password, setPassword] = useState('mudar123')

    useEffect(() => {

    }, [props]);

    async function handleLogin(e){

        e.preventDefault()

        try {

            var response = await api.post('login', { email: login, senha: password })

            console.log( response )

            //window.localStorage.setItem('token', response.data.token)
            //window.location.href="/"

        } catch ( e ) {

            //messageStore.addError(e)

        }

    }

    return(

        <div id="login">

            <form onSubmit={handleLogin}>

                <label>
                    <i className="email"></i>
                    <input placeholder="UsuÃ¡rio" autoFocus defaultValue={login} onChange={(e) => setLogin(e.target.value)}/>
                </label>

                <label>
                    <i className="password"></i>
                    <input placeholder="Senha" defaultValue={password} type="password" onChange={(e) => setPassword(e.target.value)}/>
                </label>

                <label>
                    <button>Entrar</button>
                </label>

            </form>

        </div>

    )

}
